import React from 'react'
import ReactDOM from 'react-dom'
import './App.css'

function Picture() {
  return (
    <div className = "wrapper">
      <img src="img_avatar.png" alt = "test" > </img>
    </div>
  );
}

function Subtitle() {
  return (
    <p>Cambridge, MA</p>
  ); 
}

function Biography() {
  return (
    <div className = "biographyContent">
      <p>hi, i'm adarsh (ah-dhursh). i study computer science, mathematics, and economics at harvard.</p>
      <p>currently, i'm building <a href = "https://mercor.io">mercor</a>, a software developer vetting and job placement platform. i also do research about inflation. in my free time, i help coach bellarmine college prep's policy debate debate team.</p>
      <p>previously, i was an ML research intern at IBM, SWE at Stanford CIBSR, and SWE at 1WorldOnline.</p>
      <p>i taught myself how to juggle 5 balls last summer, i can slice a banana in half by throwing playing cards, i'm learning how to DJ, i can custom make longboard decks, and i put milk in my bowl before the cereal.</p>
      <p>academic interests: computer vision, LLMs, macroeconomics, and full stack web development. </p>
      <p>skills (in order of proficiency): python (pytorch, numpy, pandas, django), java, react, typescript, AWS (S3, Lambda, EC2, Cognito, Dynamo DB, Cloudfront), mongo DB, firebase, AWS, C, MATLAB</p>
      <p>links: <a href = "https://github.com/ahiremathh">github</a>, <a href = "https://twitter.com/adarshhiremath8">twitter</a>, <a href = "https://www.linkedin.com/in/adarsh-h-623941204/">linkedin</a></p>

    </div>
  ); 
}

class Page extends React.Component {
  render() {
    return (
      <div className = "page">
        <div className = "name">
          <Picture/>
        </div>

        <div className = "subtitle">
          <Subtitle/>
        </div>

        <div className = "biography">
          <Biography/>
        </div>
      </div>
    ); 
  }
}

// ========================================

const root = ReactDOM.createRoot(document.getElementById("root"));
root.render(<Page />);
