import React from 'react'
import ReactDOM from 'react-dom'
import './App.css'

function Name() {
  return (
    <h1 className>Adarsh Hiremath</h1>
  );
}

function Subtitle() {
  return (
    <p>Cambridge, MA</p>
  ); 
}

function Biography() {
  return (
    <div className = "biographyContent">
      <p>i'm a student at Harvard studying computer science, mathematics, and economics.</p>
      <p>currently, i'm working on building <a href = "https://mercor.io">mercor</a>, a software developer vetting and job placement platform. i also do research about inflation. in my free time, i help coach bellarmine college prep's policy debate debate team.</p>
      <p>previously, i was an ML research intern at IBM, SWE at Stanford CIBSR, and SWE at 1WorldOnline.</p>
      <p>i taught myself how to juggle 5 balls last summer, i can slice a banana in half by throwing playing cards, the weeknd is my favorite artist, and i occasionally run half marathons.</p>
      <p>academically, i'm interested in computer vision, LLMs, macroeconomics, and full stack web development. </p>
      <p>my favorite</p>
      <p>contact me at ahiremath@college.harvard.edu</p>

    </div>
  ); 
}

class Page extends React.Component {
  render() {
    return (
      <div className = "page">
        <div className = "name">
          <Name/>
        </div>

        <div className = "subtitle">
          <Subtitle/>
        </div>

        <div className = "biography">
          <Biography/>
        </div>
      </div>
    ); 
  }
}

// ========================================

const root = ReactDOM.createRoot(document.getElementById("root"));
root.render(<Page />);
